import { createRouter, createWebHistory } from 'vue-router';
import Home from '@/views/Home.vue';
import PostsView from '@/views/PostsView.vue';
import CommentsView from '@/views/CommentsADD.vue';
import UsersView from '@/views/UsersView.vue';
import CreatePostComponent from '@/views/CreatePostComponent.vue';
import EditPostComponent from '@/views/EditPostComponent.vue';
import UserList from '@/views/UserList.vue';
import TodoList from '@/views/TodoList.vue';
import TodoForm from '@/views/TodoForm.vue';
import EditTodo from '@/views/EditTodo.vue';
import Login from '@/views/Login.vue';
import Register from '@/views/Register.vue';
import Profile from '@/views/Profile.vue';

// Function to check if the user is authenticated
const isAuthenticated = () => {
  return !!localStorage.getItem('token');
};

const routes = [
  { path: '/', name: 'Home', component: Home },
  { path: '/posts', name: 'Posts', component: PostsView, meta: { requiresGuest: true } },
  { path: '/comments', name: 'Comments', component: CommentsView, meta: { requiresGuest: true } },
  {
    path: '/users',
    name: 'Users',
    component: UsersView,
    meta: { requiresGuest: true },
    children: [
      { path: '', component: UserList },  // Display UserList component inside UsersView
    ],
  },
  {
    path: '/todos',
    name: 'Todos',
    component: TodoList,
    meta: { requiresGuest: true },
    children: [
      { path: '', component: TodoList },  // Display TodoList component inside TodosView
    ],
  },
  {
    path: '/create',
    name: 'CreatePost',
    component: CreatePostComponent,
    meta: { requiresGuest: true },
  },
  {
    path: '/edit/:id',
    name: 'EditPost',
    component: EditPostComponent,
    meta: { requiresGuest: true },
  },
  {
    path: '/add',
    name: 'AddTodo',
    component: TodoForm,
    props: { isEdit: false },
    meta: { requiresGuest: true },
  },
  {
    path: '/edit/:id',
    name: 'EditTodo',
    component: TodoForm,
    props: route => ({ isEdit: true, todoId: Number(route.params.id) }),
    meta: { requiresGuest: true },
  },
  {
    path: '/edit-todo/:id',
    name: 'EditTodo',
    component: EditTodo,
    props: true, // Pass route params as props to the component
    meta: { requiresGuest: true },
  },
  {
    path: '/login',
    name: 'Login',
    component: Login,
    meta: { requiresAuth: true },
  },
  {
    path: '/register',
    name: 'Register',
    component: Register,
    meta: { requiresAuth: true },
  },
  {
    path: '/profile',
    name: 'Profile',
    component: Profile,
    meta: { requiresAuth: true },
  },
];

const router = createRouter({
  history: createWebHistory(),
  routes
});

// Global navigation guard to handle authentication
router.beforeEach((to, from, next) => {
  if (to.meta.requiresAuth && !isAuthenticated()) {
    // If route requires authentication and user is not authenticated, redirect to login
    next({ name: 'Login' });
  } else if (to.meta.requiresGuest && isAuthenticated()) {
    // If route requires guest and user is authenticated, redirect to home
    next({ name: 'Home' });
  } else {
    next(); // Proceed to route
  }
});

export default router;
