// src/router/index.js
import { createRouter, createWebHistory } from 'vue-router';
import Home from '@/views/Home.vue';
import PostsView from '@/views/PostsView.vue';
import CommentsView from '@/views/CommentsADD.vue';
import UsersView from '@/views/UsersView.vue';
import CreatePostComponent from '@/views/CreatePostComponent.vue';
import EditPostComponent from '@/views/EditPostComponent.vue';
import UserList from '@/views/UserList.vue';
import TodoList from '@/views/TodoList.vue';
import TodoForm from '@/views//TodoForm.vue';
import EditTodo from '@/views/EditTodo.vue';
import Login from '@/views/Login.vue';
const routes = [
  { path: '/', name: 'Home', component: Home },
  { path: '/posts', name: 'Posts', component: PostsView },
  { path: '/comments', name: 'Comments', component: CommentsView },
  {
    path: '/users',
    name: 'Users',
    component: UsersView,
    children: [
      { path: '', component: UserList },  // Display UserList component inside UsersView
    ],
  },
  {
    path: '/todos',
    name: 'Todos',
    component: TodoList,
    children: [
      { path: '', component: TodoList },  // Display TodoList component inside TodosView
    ],
  },
  {
    path: '/create',
    name: 'CreatePost',
    component: CreatePostComponent,
  },
  {
    path: '/edit/:id',
    name: 'EditPost',
    component: EditPostComponent,
  }
  ,
  {
    path: '/add',
    name: 'AddTodo',
    component: TodoForm,
    props: { isEdit: false }
  },
  {
    path: '/edit/:id',
    name: 'EditTodo',
    component: TodoForm,
    props: route => ({ isEdit: true, todoId: Number(route.params.id) })
  }
  ,
  {
    path: '/edit-todo/:id',
    name: 'EditTodo',
    component: EditTodo,
    props: true // Pass route params as props to the component
  }
  ,
  {
    path: '/login',
    name: 'Login',
    component: Login,
    props: { isEdit: false }
  },
  { path: '/login', component: Login },
    { path: '/register', component: Register },
    { path: '/profile', component: Profile },
    { path: '*', redirect: '/login' }
];

const router = createRouter({
  history: createWebHistory(),
  routes
});


export default router;
