// src/router/index.js
import { createRouter, createWebHistory } from 'vue-router';
import Home from '@/views/Home.vue';
import PostsView from '@/views/PostsView.vue';
import CommentsView from '@/views/CommentsView.vue';
import UsersView from '@/views/UsersView.vue';
import TodosView from '@/views/TodosView.vue';
import CreatePostComponent from '@/views/CreatePostComponent.vue';
import EditPostComponent from '@/views/EditPostComponent.vue';
import UserList from '@/views/components/Users.vue';
import TodoList from '@/components/Todos.vue';
const routes = [
  { path: '/', name: 'Home', component: Home },
  { path: '/posts', name: 'Posts', component: PostsView },
  { path: '/comments', name: 'Comments', component: CommentsView },
  { path: '/users', name: 'Users', component: UsersView },
  { path: '/todos', name: 'Todos', component: TodosView },
  {
    path: '/create',
    name: 'CreatePost',
    component: CreatePostComponent
  }
  ,
  {
    path: '/edit/:id',
    name: 'EditPost',
    component: EditPostComponent
  },
  { path: '/users', component: Users },
  { path: '/todos', component: Todos },
];

const router = createRouter({
  history: createWebHistory(),
  routes
});

export default router;
